import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";

const EditPortofolio = () => {
  const [title, setTitle] = useState("");
  const [price, setPrice] = useState("");
  const [text, setText] = useState("");
  const [msg, setMsg] = useState("");
  const { id } = useParams();
  const Navigate = useNavigate();

  useEffect(() => {
    getCctv();
  }, []);

  const getCctv = async () => {
    const response = await axios.get(`http://localhost:5000/portofolio/${id}`);
    setTitle(response.data.judul);
    setPrice(response.data.harga);
    setText(response.data.specifikasi);
  };

  const updateCctv = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.patch(
        `http://localhost:5000/portofolio/${id}`, {
          judul,
          harga,
          specifikasi
        });
      Navigate("/dashboard");
    } catch (error) {
      if (error.response) {
        setMsg(error.response.data.msg);
      }
    }
  };

  return (
    <div className="columns is-centered mt-5">
      <div className="column is-half">
        {msg && (
          <div class="notification is-danger" onChange={msg}>
            <strong className="is-centered">{msg}</strong>
          </div>
        )}
        <form onSubmit={updatePortofolio}>
          <div className="field">
            <label className="label">Judul</label>
            <div className="control">
              <input
                type="text"
                className="input"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                placeholder="Judul Portofolio"
              />
            </div>
          </div>
          <div className="field">
            <label className="label">location</label>
            <div className="control">
              <input
                type="text"
                className="input"
                value={location}
                onChange={(e) => setLocation(e.target.value)}
                placeholder="location"
              />
            </div>
          </div>
          <div className="field">
            <label className="label">Keterangan</label>
            <div className="control">
              <input
                type="text"
                className="input"
                value={text}
                onChange={(e) => setText(e.target.value)}
                placeholder="text"
              />
            </div>
          </div>
          <div className="field">
            <label className="label">Image</label>
            <div className="control">
              <div className="file">
                <label className="file-label">
                  <input
                    type="file"
                    className="file-input"
                    onChange={loadImage}
                  />
                  <span className="file-cta">
                    <span className="file-label">Choose a File</span>
                  </span>
                </label>
              </div>
            </div>
          </div>
          {preview ? (
            <figure className="image is-140x140">
              <img src={preview} alt="preview" />
            </figure>
          ) : (
            ""
          )}
          <br />
          <div className="field">
            <button type="submit" className="button is-success">
              Update
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default EditPortofolio;
