import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AddPortofolio = () => {
  const [title, setTitle] = useState("");
  const [location, setLocation] = useState("");
  const [text, setText] = useState("");
  const [file, setFile] = useState("");
  const [msg, setMsg] = useState("");
  const Navigate = useNavigate();

  const [preview, setPreview] = useState("");

  const loadImage = (e) => {
    const image = e.target.files[0];
    setFile(image);
    setPreview(URL.createObjectURL(image));
  };

  const savePortofolio = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("file", file);
    formData.append("title", title);
    formData.append("location", location);
    formData.append("text", text);
    try {
      const response = await axios.post(
        "http://localhost:5000/portofolio",
        formData,
        {
          headers: {
            "Content-type": "multipart/form-data",
          },
        }
      );
      Navigate("/dashboard");
    } catch (error) {
      if (error.response) {
        setMsg(error.response.data.msg);
      }
    }
  };

  return (
    <div className="columns is-centered mt-5">
      <div className="column is-half">
        {msg && (
          <div class="notification is-danger" onChange={msg}>
            <strong className="is-centered">{msg}</strong>
          </div>
        )}
        <form className="box" onSubmit={savePortofolio}>
          <div className="field">
            <label className="label">Judul</label>
            <div className="control">
              <input
                type="text"
                className="input is-link"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                placeholder="Judul Portofolio"
              />
            </div>
          </div>
          <div className="field">
            <label className="label">location</label>
            <div className="control">
              <input
                type="text"
                className="input is-link"
                value={location}
                onChange={(e) => setLocation(e.target.value)}
                placeholder="location"
              />
            </div>
          </div>
          <div className="field">
            <label className="label">Keterangan</label>
            <div className="control">
              <textarea
                value={text}
                onChange={(e) => setText(e.target.value)}
                class="textarea is-link"
                placeholder="Keterangan"
              ></textarea>
            </div>
          </div>
          <div className="field">
            <label className="label">Image</label>
            <div className="control">
              <div className="file">
                <label className="file-label">
                  <input
                    type="file"
                    className="file-input"
                    onChange={loadImage}
                  />
                  <span className="file-cta">
                    <span class="file-icon">
                      <i class="fas fa-upload"></i>
                    </span>
                    <span className="file-label">Choose a File</span>
                  </span>
                </label>
              </div>
            </div>
          </div>
          {preview ? (
            <figure className="image is-140x140">
              <img src={preview} alt="preview" />
            </figure>
          ) : (
            ""
          )}
          <br />
          <div className="field">
            <button type="submit" className="button is-success">
              Simpan
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddPortofolio;
