import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Addcctv = () => {
  const [title, setTitle] = useState("");
  const [price, setPrice] = useState("");
  const [text, setText] = useState("");
  const [msg, setMsg] = useState("");
  const Navigate = useNavigate();

  const saveCctv = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/cctv", {
        judul: title,
        harga: price,
        keterangan: text,
      });
      Navigate("/dashboard");
    } catch (error) {
      if (error.response) {
        setMsg(error.response.data.msg);
      }
    }
  };

  return (
    <div className="columns is-centered mt-5">
      <div className="column is-half">
        {msg && (
          <div class="notification is-danger" onChange={msg}>
            <strong className="is-centered">{msg}</strong>
          </div>
        )}
        <form className="box" onSubmit={saveCctv}>
          <div className="field">
            <label className="label">Judul</label>
            <div className="control">
              <input
                type="text"
                className="input is-link"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                placeholder="Judul CCTV"
              />
            </div>
          </div>
          <div className="field">
            <label className="label">Harga</label>
            <div className="control">
              <input
                type="number"
                className="input is-link"
                value={price}
                onChange={(e) => setPrice(e.target.value)}
                placeholder="Harga"
              />
            </div>
          </div>
          <div className="field">
            <label className="label">Specifikasi</label>
            <textarea
              value={text}
              onChange={(e) => setText(e.target.value)}
              class="textarea is-link"
              placeholder="Specifikasi"
            ></textarea>
          </div>
          <br />
          <div className="field">
            <button type="submit" className="button is-success">
              Simpan
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Addcctv;
