import React, { useState, useEffect } from "react";
import axios from "axios";
import jwt_decode from "jwt-decode";
import { useNavigate, Link } from "react-router-dom";

const Dashboard = () => {
  const [name, setName] = useState("");
  const [msg, setMsg] = useState("");
  const [msgError, setMsgError] = useState("");
  const [token, setToken] = useState("");
  const [expire, setExpire] = useState("");
  const [portofolio, setPortofolio] = useState([]);
  const Navigate = useNavigate();

  useEffect(() => {
    refreshToken();
    getPortofolio();
  }, []);

  const refreshToken = async () => {
    try {
      const response = await axios.get("http://localhost:5000/token");
      setToken(response.data.accessToken);
      const decoded = jwt_decode(response.data.accessToken);
      setName(decoded.name);
      setExpire(decoded.exp);
    } catch (error) {
      if (error.response) {
        Navigate("/");
      }
    }
  };

  const axiosJWT = axios.create();

  axiosJWT.interceptors.request.use(
    async (config) => {
      const currentDate = new Date();
      if (expire * 1000 < currentDate.getTime()) {
        const response = await axios.get("http://localhost:5000/token");

        config.headers.Authorization = `Bearer ${response.data.accessToken}`;

        setToken(response.data.accessToken);

        const decoded = jwt_decode(response.data.accessToken);
        setName(decoded.name);
        setExpire(decoded.exp);
      }
      return config;
    },
    (error) => {
      return Promise.reject(error);
    }
  );

  const getPortofolio = async () => {
    const response = await axiosJWT.get("http://localhost:5000/portofolio");
    setPortofolio(response.data);
  };

  const Delete = async (id) => {
    try {
      const response = await axios.delete(
        `http://localhost:5000/portofolio/${id}`
      );
      setMsg(response.data.msg);
      getPortofolio();
    } catch (error) {
      if (error.response) {
        setMsgError(error.response.data.msg);
      }
    }
  };

  return (
    <>
      <h1 className="has-text-centered mt-5">Welcome Back : {name}</h1>
      <div className="container mt-5">
        {msg && (
          <div class="notification is-success" onChange={msg}>
            <strong className="is-centered">{msg}</strong>
          </div>
        )}
        {msgError && (
          <div class="notification is-danger" onChange={msgError}>
            <strong className="is-centered">{msgError}</strong>
          </div>
        )}

        <table class="table is-bordered is-striped is-fullwidth">
          <tr class="th is-selected">
            <th>Student Name</th>
            <th>Physics</th>
            <th>Chemistry</th>
            <th>Biology</th>
            <th>Total</th>
          </tr>
          <tr>
            <td>Mike</td>
            <td>80</td>
            <td>55</td>
            <td>75</td>
            <td>210</td>
          </tr>
          <tr>
            <td>Haynes</td>
            <td>70</td>
            <td>65</td>
            <td>40</td>
            <td>175</td>
          </tr>
          <tr>
            <td>Hina</td>
            <td>70</td>
            <td>52</td>
            <td>63</td>
            <td>185</td>
          </tr>
          <tr>
            <td>Johny</td>
            <td>55</td>
            <td>53</td>
            <td>42</td>
            <td>150</td>
          </tr>
        </table>

        <h1 className="has-text-centered">Table Portofolio</h1>
        <div className="columns is-multiline">
          {portofolio.map((porto) => (
            <div className="column is-one-quarter" key={porto.id}>
              <div className="card">
                <div className="card-image">
                  <figure className="image is-4by3">
                    <img src={porto.url} alt="Image" />
                  </figure>
                </div>
                <div className="card-content">
                  <div className="media">
                    <div className="media-content">
                      <p className="title is-4 has-text-centered">
                        {porto.judul}
                      </p>
                      <p className=" is-6">Alamat : {porto.alamat}</p>
                      <p className="subtitle is-6">
                        Keterangan : {porto.keterangan}
                      </p>
                    </div>
                  </div>
                </div>
                <footer className="card-footer">
                  <Link
                    to={`/editportofolio/${porto.id}`}
                    className="button is-success card-footer-item"
                  >
                    Edit
                  </Link>
                  <Link
                    onClick={() => Delete(porto.id)}
                    className="button is-danger card-footer-item"
                  >
                    Delete
                  </Link>
                </footer>
              </div>
            </div>
          ))}
        </div>
      </div>
    </>
  );
};

export default Dashboard;
